            all_difficulties_question_ids = Question.all_in(:tag_ids.in=>[difficulty_tags[difficulty_level],chapter_data['guid']]).map(&:id)
            subjects.each do |subject_name|
              question_sets[subject_name][difficulty_level] << Question.where(:id.in=>all_difficulties_question_ids,:tag_ids.in=>subject_tags[subject_name]).map(&:id)
            end
          end
        end
      end

      subjects.each do |subject_name|
        no_of_questions = params['no_of_questions'].to_i/3
        pre_final_question_ids = []

        if difficulty_levels.count == 1
          questions_count = [no_of_questions]
        elsif difficulty_levels.count == 2
          questions_count = [no_of_questions/2,no_of_questions-(no_of_questions/2)]
        else
          questions_count = [no_of_questions/3,no_of_questions/3,no_of_questions-(no_of_questions/3)*2]
        end

        question_sets[subject_name].values.each_with_index do |a_of_a,i|
          a_of_a = a_of_a - [[]]
          question_ids = []
          select_questions_per_chapter = a_of_a.count/questions_count[i]
          a_of_a.each do |a|
            question_ids << a.sample(select_questions_per_chapter)
          end

          question_ids = question_ids.flatten
          if question_ids.count < questions_count[i]
            question_ids = question_ids + (a_of_a.flatten - question_ids).sample(questions_count[i] - question_ids.count)
          elsif question_ids.count > questions_count[i]
            question_ids = question_ids - question_ids.sample(question_ids.count - questions_count[i])
          end

          pre_final_question_ids << question_ids
        end

        pre_final_question_ids = pre_final_question_ids.flatten
        if pre_final_question_ids.count < no_of_questions  #include any question from the subject and within chapters selected
          subject_level_all_questions = question_sets[subject_name].values.map{|a| a - [[]]}.flatten
          pre_final_question_ids = pre_final_question_ids + (subject_level_all_questions - pre_final_question_ids).sample(no_of_questions - pre_final_question_ids.count)
        end

        if pre_final_question_ids.count < no_of_questions #include any question from the subject
          global_subject_level_all_questions =  Question.where(:tag_ids.in=>subject_tags[subject_name]).map(&:id)
          pre_final_question_ids = pre_final_question_ids + (global_subject_level_all_questions - pre_final_question_ids).sample(no_of_questions - pre_final_question_ids.count)
        end

        final_question_ids[subject_name] = pre_final_question_ids
      end

      total_marks = Question.where(:id.in=>final_question_ids.values.flatten).map{|q| q.default_mark}.sum

      quiz = Quiz.create!(quiz_language_specific_datas_attributes: [{name:params['name'],description:params['description'],instructions:params['instructions'],language: 'english'}],type:params['quiz_type'], player:params['quiz_type'], total_marks:total_marks, total_time:params['duration'],created_by:current_user.id)

      quiz_section_ids = []
      subjects.each do |subject_name|
        quiz_section = QuizSection.create!(question_ids:final_question_ids[subject_name], quiz_id: quiz.id.to_s,quiz_section_language_specific_datas_attributes: [{name:subject_name,instructions:'', language: 'english'}])
        quiz_section_ids << quiz_section.id.to_s
      end

      quiz.quiz_section_ids = quiz_section_ids
      quiz.save!

      quiz.quiz_json = quiz.as_json(with_key:true,with_language_support:false)
      quiz.final = true
      quiz.tags_verified = true
      quiz.save!

      data = {'success'=>true,'asset_download_id'=>quiz.guid,'assessment_guid'=>quiz.id,'test_download_id'=>quiz.id}
    else
      question_sets = {}
      difficulty_levels.each do |difficulty_level|
        question_sets[difficulty_level] = []
      end

      params['chapters'].each do |chapter_data|
        if false #chapter_data['concepts'].present?
        else
          difficulty_levels.each do |difficulty_level|
            question_sets[difficulty_level] << Question.all_in(:tag_ids.in=>[difficulty_tags[difficulty_level],chapter_data['guid']]).map(&:id)
          end
        end
      end

      no_of_questions = params['no_of_questions'].to_i
      final_question_ids = []

      if difficulty_levels.count == 1
        questions_count = [no_of_questions]
      elsif difficulty_levels.count == 2
        questions_count = [no_of_questions/2,no_of_questions-(no_of_questions/2)]
      else
        questions_count = [no_of_questions/3,no_of_questions/3,no_of_questions-(no_of_questions/3)*2]
      end

      question_sets.values.each_with_index do |a_of_a,i|
        question_ids = []
        select_questions_per_chapter = a_of_a.count/questions_count[i]
        a_of_a.each do |a|
          question_ids << a.sample(select_questions_per_chapter)
        end

        question_ids = question_ids.flatten
        if question_ids.count < questions_count[i]
          question_ids = question_ids + (a_of_a.flatten - question_ids).sample(questions_count[i] - question_ids.count)
        elsif question_ids.count > questions_count[i]
          question_ids = question_ids - question_ids.sample(question_ids.count - questions_count[i])
        end

        final_question_ids << question_ids
      end

      final_question_ids = final_question_ids.flatten

      if final_question_ids.count < no_of_questions
        all_questions_from_all_subjects = question_sets.values.map{|a| a - [[]]}.flatten
        final_question_ids = final_question_ids + (all_questions_from_all_subjects - final_question_ids).sample(no_of_questions - final_question_ids.count)
      end

      total_marks = Question.where(:id.in=>final_question_ids).map{|q| q.default_mark}.sum

      quiz = Quiz.create!(quiz_language_specific_datas_attributes: [{name:params['name'],description:params['description'],instructions:params['instructions'],language: 'english'}],question_ids:final_question_ids,type:params['quiz_type'], player:params['quiz_type'], total_marks:total_marks, total_time:params['duration'],created_by:current_user.id)
      quiz.quiz_json = quiz.as_json(with_key:true,with_language_support:false)
      quiz.final = true
      quiz.tags_verified = true
      quiz.save!

      data = {'success'=>true,'asset_download_id'=>quiz.id,'assessment_guid'=>quiz.id,'test_download_id'=>quiz.id}
    end

    render json: data
  end

  def get_quiz_json
    data = Quiz.where(guid:params[:assessment_guid])[0].quiz_json
    render json: data
  end

  def get_question_json
    render json: Question.find(params[:id]).as_json(with_key:true,with_language_support:false)
  end

  def publish_assessment
    data = {}
    begin
      quiz_targeted_group_data = {password:params[:password], time_open:params[:time_open].to_i, time_close:params[:time_close].to_i, show_score_after:params[:show_score_after], show_answers_after:params[:show_answers_after], published_by:current_user.id, group_ids:params[:group_ids], user_ids:params[:user_ids], message_subject:params[:message_subject], message_body:params[:message_body],quiz_id:params[:quiz_id],shuffle_questions:params[:shuffle_questions],shuffle_options:params[:shuffle_options],pause:params[:pause],max_no_of_attempts:params[:max_no_of_attempts]}

      qtg = QuizTargetedGroup.create!(quiz_targeted_group_data)

      if qtg.present?
        data['publish_id'] = qtg.id
        data['success'] = true
      else
        data['success'] = false
      end
    rescue
      data['success'] = false
    end

    render json: data
  end

end